    return (
      <div>
        <h3>1.4 JavaScript in JSX with Curly Braces</h3>
      </div>
    );
  }

const today = new Date();

function formatDate(date) {
  return new Intl.DateTimeFormat(
    'en-US',
    { weekday: 'long' }
  ).format(date);
}

function MyDay() {
  return (
    <h1>Today is a {formatDate(today)}</h1>
  );
}

